export function StreamHomeContent({
  dictionary,
  lang,
  schoolId
}: StreamContentProps) {
  // Get features from dictionary or use defaults
  const features: Feature[] = dictionary?.home?.features || [
    {
      title: "Comprehensive Courses",
      description: "Access a wide range of carefully curated courses designed by industry experts.",
      icon: "",
    },
    {
      title: "Interactive Learning",
      description: "Engage with interactive content, quizzes, and assignments to enhance your learning experience.",
      icon: <InteractiveLearningIcon />,
    },
    {
      title: "Progress Tracking",
      description: "Monitor your progress and achievements with detailed analytics and personalized dashboards.",
      icon: "",
    },
    {
      title: "Community Support",
      description: "Join a vibrant community of learners and instructors to collaborate and share knowledge.",
      icon: "",
    },
  ];

  const isRTL = lang === "ar";

  return (
    <>
      {/* Hero Section with Animation */}
      <section className="relative">
        <div className={`flex flex-col lg:flex-row items-center gap-8 lg:gap-16 ${isRTL ? "lg:flex-row-reverse" : ""}`}>
          {/* Text Content */}
          <div className={`flex-1 flex flex-col space-y-6 ${isRTL ? "items-end text-right" : "items-start text-left"} lg:items-start lg:text-left`}>
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-extrabold tracking-tighter leading-none">
              {dictionary?.home?.title || "Lumos"}
              <br />
              <span className="text-3xl md:text-4xl lg:text-5xl font-semibold block mt-2">
                {dictionary?.home?.description || "Shine a light."}
              </span>
            </h1>

            <div className={`flex flex-col sm:flex-row gap-4 ${isRTL ? "sm:flex-row-reverse" : ""}`}>
              <Link
                className={buttonVariants({
                  size: "lg",
                })}
                href={`/${lang}/stream/courses`}
              >
                {dictionary?.home?.exploreCourses || "Explore Courses"}
              </Link>

              <Link
                className={buttonVariants({
                  size: "lg",
                  variant: "ghost",
                })}
                href={`/${lang}/login`}
              >
                {dictionary?.home?.signIn || "Sign in"}
              </Link>
            </div>
          </div>

          {/* Animation */}
          <div className="flex-1 flex justify-center">
            <EducationAnimation className="w-full max-w-md h-58 md:h-70" />
          </div>
        </div>
      </section>

      <section className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-32">
        {features.map((feature, index) => (
          <Card key={index} className="shadow-none border hover:border-foreground transition-colors">
            <CardHeader>
              <div className={`text-4xl mb-4 ${isRTL ? "text-right" : "text-left"}`}>
                {feature.title === "Comprehensive Courses" ? <ComprehensiveCoursesIcon /> :
                 feature.title === "Interactive Learning" ? <InteractiveLearningIcon /> :
                 feature.title === "Community Support" ? <CommunityIcon /> :
                 feature.title === "Progress Tracking" ? <ProgressTrackingIcon /> :
                 feature.icon}
              </div>
              <CardTitle className={isRTL ? "text-right" : "text-left"}>
                {feature.title}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p className={`text-muted-foreground ${isRTL ? "text-right" : "text-left"}`}>
                {feature.description}
              </p>
            </CardContent>
          </Card>
        ))}
      </section>

      <AIFluencySection dictionary={dictionary} lang={lang} />

      <SkillsSection dictionary={dictionary} lang={lang} />

      <HotReleasesSection dictionary={dictionary} lang={lang} />

      <CurriculumSection dictionary={dictionary} lang={lang} />

      <TeachingHeroSection dictionary={dictionary} lang={lang} />

      <ReasonsSection dictionary={dictionary} lang={lang} />

      <HowToBeginSection dictionary={dictionary} lang={lang} />
    </>
  );
}